import * as types from '../type/index';
//import { findIndex_v1, search } from '../../hooks';

const initialState =[]

const reducerOrder = (state = initialState, action) => {
  const { type, payload } = action;  
//   var temp = -1
  switch (type) {
    case types.ADD_ORDER:
      let updatedState;

      if (!state || state.length === 0) {
        updatedState = payload ? [...payload] : [];
      } else {
        updatedState = payload ? [...state, ...payload] : [...state];
      }

      localStorage.setItem('orders', JSON.stringify(updatedState));
      return updatedState;
      // if (!state || state.length === 0) {
      //   return payload ? [...payload] : [];
      // } else {
      //   return payload ? [...state, ...payload] : [...state];
      // }
    case types.DELETE_ORDER:
      if (!payload) return state;
      const updatedStates = state.filter(item => item.itemCode + item.variant !== payload);
      localStorage.setItem('orders', JSON.stringify(updatedStates));
      return updatedStates;
    default:
      return state
  }
}

export default reducerOrder;
