import { getApi, postApi } from "../../api";
import * as Types from "../type/index"

/* đăng nhập */
export const getLogin = (formData) => {
  return (dispatch) => {
    postApi("login", formData).then((result) =>{
      return dispatch({ type: Types.LOGIN, payload: result.data[0].Code })
    })
  }
};

/* get kho theo user */
export const getLocationByUser = (userCode) => {
  return (dispatch) => {
    getApi(`location/${userCode}`).then((result) =>{
      return dispatch({ type: Types.GETLOCATIONBYUSER, payload: result.data })
    })
  }
};

/* lấy danh sách thuế */
export const getTax = () => {
  return (dispatch) => {
      getApi("tax").then((result) =>{
        return dispatch({ type: Types.GETTAX, payload: result.data })
      })
  }
};

/* lấy danh hóa đơn */
export const getBillPSI = (formData) => {
  return (dispatch) => {
    postApi("getBillPSI", formData).then((result) =>{
        return dispatch({ type: Types.GETBILLL, payload: result.data })
    })
  }
};

export const getBillPTS = (formData) => {
  return (dispatch) => {
    postApi("getBillPTS", formData).then((result) =>{
        return dispatch({ type: Types.GETBILLL, payload: result.data })
    })
  }
};

/* update trạng thái hóa đơn */
export const updateStatusBill = (id, staff, number) => {
  return (dispatch) => {
    return dispatch({ type: Types.UPDATE_STATUS_BILL, payload: {id, staff, number} })
  }
};

/* update trạng thái hóa đơn nhân viên */
export const updateStatusBillByStaff = (id, img) => {
  return (dispatch) => {
    return dispatch({ type: Types.GET_BILL_BY_STAFF_UPDATE_BILL, payload: {id, img} })
  }
};

/* fiter trạng thái hóa đơn */
export const fiterStatusBill = (status) => {
  return (dispatch) => {
    return dispatch({ type: Types.FITER_STATUS, payload: status })
  }
};

/* reset hóa đơn */
export const resetBill = () => {
  return (dispatch) => {
    return dispatch({ type: Types.RESET_BILL })
  }
};

/* get tất cả nhân viên giao hàng */
export const getStaffAll = () => {
  return (dispatch) => {
    getApi("getStaff").then((result) =>{
      return dispatch({ type: Types.GET_STAFFALL, payload: result.data })
    })
  }
};

/* get menu */
export const getMenu = (formData) => {
  return (dispatch) => {
    postApi("getMenuByUser", formData).then((result) =>{
      return dispatch({ type: Types.GETMENU, payload: result.data })
    })
  }
};
