import { combineReducers } from 'redux'
import reducerUser from './user';
import reducerBill from './bill';
import reducerLogin from './login';

const rootReducer = combineReducers({
  user: reducerUser,
  bill: reducerBill,
  login: reducerLogin,
})

export default rootReducer;
