import * as types from '../type/index';
import { findIndex_v1 } from '../../hooks';

const initialState =[]
const reducerWSOutOfTheWarehouse = (state = initialState, action) => {
  const { type, payload } = action;  
  var temp = -1
  switch (type) {
    case types.GET_WS_OUT_OF_THE_WAREHOUSE:  
      payload ? state = payload : state = []
      return [...state] 
    case types.GET_BILL_BY_STAFF_UPDATE_BILL:  
      temp = findIndex_v1(state, payload.Code)
      if(temp !== -1){
        state[temp].Status = "1"
        state[temp].Image = payload.img
      } 
      return [...state] 
    default:
      return state
  }
}

export default reducerWSOutOfTheWarehouse;
