import { useState } from 'react'
import { getApi } from '../../api';
function ModalItem( props ) {
    const [order, setOrder] = useState();
    const handeChange = (orderNo) => {
        getApi("getOrderDetail/"+orderNo).then((res)=>{
            setOrder(res.data);
        })
    }
    console.log(order);
    return (
      props.orderHeader &&
      props.orderHeader.map((item,index) => {
      return (
        <tr key={index}>
           <td>{item.STT}</td>
           <td>{item.KhuVuc}</td>
           <td value={item.MaDH} onClick={()=>handeChange(item.MaDH)}  data-bs-dismiss="modal">{item.MaDH}</td>
           <td>{item.MaKH}</td>
           <td>{item.TenKH}</td>
           <td>{item.TrangThai}</td>
        </tr>
      )
    })
   
  );
}

export default ModalItem;
