import * as types from '../type/index';

const initialState =[]
const reducerOrder = (state = initialState, action) => {
  const { type, payload } = action;  
  switch (type) {
    case types.GET_CART_BY_USER:
      payload ? state = payload :  state = []
      return [...state] 
    case types.ADD_ORDER:
      if (!state || state.length === 0) {
        return payload ? [...payload] : [];
      } else {
        return payload ? [...state, ...payload] : [...state];
      }
    case types.DELETE_ORDER:
      if (payload == null) return state; // Check null/undefined
      const index = state.findIndex(item => item.id === payload);
      if (index === -1) return state; // Không tìm thấy → giữ nguyên state
      return [
      ...state.slice(0, index),   // Phần trước index
      ...state.slice(index + 1)   // Phần sau index (bỏ qua phần tử cần xóa)
      ];
    default:
      return state
  }
}

export default reducerOrder;
