import { memo } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

function Modal(props) {
  const { detailOrder, note, onChangeNote, handeModalUpdateStatus, handeModalCancel } = props;  
  const renderActionButtons = () => {
    if (detailOrder.length === 0) return null;
    switch (detailOrder[0].Status) {
      case '2':
        return (<> 
                  <button type="button" className="btn btn-outline-dark" style = {styles.text} data-bs-dismiss={ note.note ? "modal" : "" } onClick = {handeModalCancel}>Hủy</button>
                  <button type="button" className="btn btn-outline-success" style = {styles.text} data-bs-dismiss={ note.note ? "modal" : "" } onClick = {handeModalUpdateStatus}>Duyệt</button>
                </>);
      case '3':
        return (<button type="button" className="btn btn-outline-dark" style = {styles.text} data-bs-dismiss={ note.note ? "modal" : "" } onClick = {handeModalCancel}>Hủy</button>);
      default:
        return null;
    }
  };

  return (
    <div className="modal fade" id="detail" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={styles.text} id="staticBackdropLabel">Chi tiết đặt hàng</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                  <div className="row g-3">
                    <div className="col-md-12 col-sm-12">
                      <label className="form-label" style = {styles.text}>Ngày cần hàng: {detailOrder[0] ? detailOrder[0].PostingDate : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Mã: {detailOrder[0] ? detailOrder[0].DocumentNo : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Mục đích sử dụng: {detailOrder[0] ? detailOrder[0].IntendedUse : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Nhà cung cấp: {detailOrder[0] ? detailOrder[0].Supplier : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Người tạo: {detailOrder[0] ? detailOrder[0].User : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Phòng ban: {detailOrder[0] ? detailOrder[0].Department : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Ngày tạo: {detailOrder[0] ? detailOrder[0].CreatedDate : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Ghi chú đặt hàng: {detailOrder[0] ? detailOrder[0].Note : ''}</label><br/>
                      <label className="form-label" style = {styles.text}>Ghi chú duyệt hàng: {detailOrder[0] ? detailOrder[0].NoteApprove : ''}</label>
                    </div>
                    <div className="col-md-12 col-sm-12">
                      <label className="form-label" style={styles.InputText}>Ghi chú</label>
                      <textarea className="form-control" name="note" rows="2" value={note.note || ''} onChange={(event)=>onChangeNote(event)}/>
                    </div>
                  </div>
                  <br/>
                  <DataTable value={detailOrder}
                             emptyMessage="Không có dữ liệu" style={styles.dataTable}>
                    <Column field="ItemCode" header="Mã hàng"  style={{ width: '10%' }}></Column>
                    <Column field="Variant" header="Mã màu" style={{ width: '10%' }}></Column>
                    <Column field="ItemName" header="Tên hàng" style={{ width: '60%' }}></Column>
                    <Column field="Unit" header="Đvt" style={{ width: '10%' }}></Column>
                    <Column field="Quantity" header="Số lượng" style={{ width: '5%' }}></Column>
                    <Column field="Price" header="Giá" style={{ width: '5%' }}></Column>
                  </DataTable>
                </div>
              </div>
              <div className="modal-footer">
                { renderActionButtons() }
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" style={styles.text}>Thoát</button>
              </div>
            </div>
          </div>
    </div>
  )
}

export default memo(Modal);

const styles = {
  bodyModal: {
    borderRadius: "10px",
    boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
    padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  },
  dataTable: {fontFamily:"Times New Roman"},
}
