import { getApi, postApiv1 } from "../../api";
import * as Types from "../type/index"

/* ================== thuế ================== */

/* lấy danh sách thuế */
export const getTax = () => {
  return (dispatch) => {
      getApi("tax").then((result) =>{
        return dispatch({ type: Types.GETTAX, payload: result.data })
      })
  }
};

/* ================== thuế ================== */

/* ================== user ================== */

/* get name user */
export const getUser = () => {
  return (dispatch) => {
    getApi("user").then((result) =>{
      return dispatch({ type: Types.GETUSER, payload: result.data })
    })
  }
};

/* ================== user ================== */

/* ================== menu ================== */

/* get menu */
export const getMenu = () => {
  return (dispatch) => {
    getApi("getMenuByUser").then((result) =>{
      return dispatch({ type: Types.GETMENU, payload: result.data })
    })
  }
};

/* ================== menu ================== */

/* ================== staff ================== */

/* get staff by user */
export const getStaffByUser = () => {
  return (dispatch) => {
    getApi("getStaff").then((result) =>{
      return dispatch({ type: Types.GETSTAFF, payload: result.data })
    })
  }
};

/* lấy danh hóa đơn */
export const getBill = (formData) => {
  return (dispatch) => {
    postApiv1("getBill", formData).then((result) =>{
        return dispatch({ type: Types.GETBILLL, payload: result.data })
    })
  }
};

/* update trạng thái hóa đơn */
export const updateStatusBill = (Code, staff, number) => {
  return (dispatch) => {
    return dispatch({ type: Types.UPDATE_STATUS_BILL, payload: {Code, staff, number} })
  }
};


