import { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import * as ACTIONS from '../../store/actions/index'
import Table from './table'
import Modal from './modal'
import { getApi } from '../../api'
function Home() {
    
    const [inputs, setInputs] = useState({})
    const [message, setMessage] = useState()

    const dispatch = useDispatch()

    const location= useSelector((state) => state.location); /* kho */
    const order = useSelector((state) => state.order); /* đơn hàng */
    const orderLine = useSelector((state) => state.orderLine); /* đơn hàng Line*/
    
    /* set input đưa vào setInputs  */ 
    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    /* set date */
    useEffect(() => {
        let date = new Date()
        var y = date.getFullYear()
        var m = (date.getMonth() + 1)
        var d = "";
        if(date.getDate() > 9){
            d = date.getDate()
        }else{
            d = "0"+date.getDate()
        }
        setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
    }, [])

    /* get kho */
    useEffect(() => {
        dispatch(ACTIONS.getLocation()) 
     },[dispatch])
    
     /* get don hang */
    const handeOrderHeader = (locationCode,formDate,toDate) => {
        locationCode && formDate && toDate ?
        dispatch(ACTIONS.getOrderHeader(locationCode,formDate,toDate)) :
        setMessage("Thiếu thông tin")
    }
    
    /* get chi tiet don hang */
    const handeOrderLine = (orderNo) => {
        orderNo ? 
        dispatch(ACTIONS.getOrderLine(orderNo)) :
        alert("Thiếu thông tin : mã đơn hàng")
    }
    
    
    /* thay doi trang thai chi tiet don hang */
    const handeChangeStatusOrderLine = (codeOrder,codeItem,codeLot) => {
        codeOrder && codeItem && codeLot ?
        dispatch(ACTIONS.setStatusOrderLine(codeOrder,codeItem,codeLot))
        : alert("Thiếu thông tin : lô")
    }
    
    /* thay doi trang thai don hang */
    const handeChangeFinish = (e) => {
        orderLine && orderLine.length > 0 ?
        getApi(`updateStatusOrderHeader/${orderLine[0].MaDH}`).then((result) =>{
            setMessage(result.data[0].Message)
        }) : alert("Thiếu thông tin : mã đơn hàng")
    }
    
    return ( 
    <div className="row" style={styles.container}>
        <form className="row g-3">
            <div className="col-md-6">
                <label className="form-label">Từ ngày</label>
                <input type="date" className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6">
                <label className="form-label">Đến ngày</label>
                <input type="date" className="form-control" name="toDate" value={inputs.toDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-12">
                <label className="col-form-label">Kho</label>
                    <select className="form-control" name="location" id="location" onChange={(event)=>onChange(event)}>
                        { location && 
                          location.map((item,index) => {
                            return (<option value={item.NO} key={index}>{item.Name}</option>)
                          })
                        }
                    </select>
            </div>
        </form>
        <div className="col-12">
            <div className="col-md-12" style={styles.padding}>
                {/* Button modal */}
                <button type="button" className="btn btn-primary" onClick={()=>handeOrderHeader(inputs.location,inputs.formDate,inputs.toDate)} data-bs-toggle="modal" data-bs-target="#staticBackdrop" style={styles.button}>
                    Chọn đơn hàng
                </button>
            </div>
            <Modal orderHeader = { order ? order : null } handeOrderLine = { handeOrderLine }/>
        </div>
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">Mã sản phẩm</th>
                    <th scope="col">Tên sản phẩm</th>
                    <th scope="col">Màu sắc</th>
                    <th scope="col">Lô</th>
                    <th scope="col">Số lượng</th>
                    <th scope="col">Đơn vị tính</th>
                    <th scope="col">Trạng thái</th>
                </tr>
            </thead>
            <tbody>
                <Table orderLine = { orderLine ? orderLine : null } handeChangeStatusOrderLine = { handeChangeStatusOrderLine } />
            </tbody>
        </table>
        <button className="btn btn-outline-dark d-flex justify-content-center" 
                data-bs-toggle="modal" data-bs-target="#ThongBao" 
                onClick={(e) => handeChangeFinish(e)}>Hoàn tất đơn soạn</button>
        
        <div className="position-fixed bottom-0 end-0 p-3" style={{zIndex: 11}}>
          <div id="liveToast" className="toast hide" role="alert" aria-live="assertive" aria-atomic="true">
            <div className="toast-header">
              {/* {<img src="..." className="rounded me-2" alt="..." />} */}
              <strong className="me-auto">Thông báo</strong>
              {/* <small>ago</small> */}
              <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close" />
            </div>
            <div className="toast-body">
              {message}
            </div>
          </div>
        </div>
        {/* Modal */}
        <div className="modal" id="ThongBao" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">Thông báo</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                  <span>{message}</span>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              </div>
            </div>
          </div>
        </div>
        {/* Modal */}
    </div>
    );
}

export default Home;

const styles = {
    title:{
      textAlign:"center",
      color:"red",
      padding:"20px"
    },
    container:{
      fontFamily:"Times New Roman",
      fontSize:"16px",
      marginBottom: "10px"
    },
    button : {
      padding:"15px",
      borderRadius:"5px",
      margin: "0 auto",
      display: "flex",
      justifyContent: "center"
    },
    padding:{
      padding:"50px"
    }
}
