import React, { useState, useEffect, useRef } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';

import { useSelector, useDispatch } from 'react-redux'
import * as ACTIONS from '../../store/actions/index'

function Tax() {
    const tax = useSelector((state) => state.tax); /* thuế */
    const dispatch = useDispatch();

    const [globalFilter, setGlobalFilter] = useState(null);
    const dt = useRef(null);

    /* get thuế */
    useEffect(() => {
        dispatch(ACTIONS.getTax()) 
     },[dispatch])

    /* xuất Excel */
    const exportCSV = () => {
        dt.current.exportCSV();
    };

    const renderHeader = () => {
        return (
            <div className="flex justify-content-between">
                <span className="p-input-icon-left">
                    <i className="pi pi-search" />
                    <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
                </span>
                <Button style={styles.margin_left} label="Export" icon="pi pi-upload" className="p-button-help" onClick={exportCSV} />
            </div>
        );
    };

    const header = renderHeader();
    return ( 
    <div className="row">
        <div className="col-12 col-sm-12">
            <h3 style={styles.title}>524 DN rủi ro theo công văn số 1798/TCT-TTKT ngày 16/5/2023</h3> 
            <DataTable ref={dt} value={tax} paginator showGridlines rows={50} 
                     globalFilter={globalFilter} header={header}
                    emptyMessage="Không có dữ liệu" style={styles.dataTable}>
                <Column field="STT" header="Số thứ tự" sortable style={{ width: '10%' }}></Column>
                <Column field="CODE" header="Mã số thuế" sortable style={{ width: '10%' }}></Column>
                <Column field="NAME" header="Tên người nộp thuế" sortable style={{ width: '60%' }}></Column>
                <Column field="CITY" header="Tên tỉnh/Thành phố" sortable style={{ width: '20%' }}></Column>
            </DataTable>
        </div>
       
    </div>
    );
}

export default Tax;

const styles = {
    title:{
      textAlign:"center",
      color:"red",
      padding:"20px",
      fontFamily:"Times New Roman"
    },
    margin_left:{
        margin:"5px",
        fontFamily:"Times New Roman"
    },
    dataTable:{
        fontFamily:"Times New Roman"
    },
    InputText:{
        fontFamily:"Times New Roman"
    }
}
