import * as types from '../type/index';

const initialState =[];
const reducerUser = (state = initialState, action) => {
  const { type, payload } = action;  
  switch (type) {
    case types.GETUSER: 
      console.log(payload[0].name);
    
      payload ? state = payload :  state = []
      return [...state] 
    default:
      return [...state]   
  }
}

export default reducerUser;

