import React, { useState, useRef, useEffect } from 'react';
import './MultiSelectWithCheckbox.css'; // (tạo file css bên dưới)

const optionsData = [
  { value: 'admin', label: 'Administrator' },
  { value: 'sales', label: 'Sales' },
  { value: 'approver', label: 'Approver' },
  { value: 'viewer', label: 'Viewer' }
];

const MultiSelectWithCheckbox = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState([]);
  const [search, setSearch] = useState('');
  const dropdownRef = useRef(null);

  const toggleOption = (value) => {
    setSelected((prev) =>
      prev.includes(value)
        ? prev.filter((v) => v !== value)
        : [...prev, value]
    );
  };

  const isAllSelected = selected.length === optionsData.length;

  const toggleSelectAll = () => {
    if (isAllSelected) {
      setSelected([]);
    } else {
      setSelected(optionsData.map((opt) => opt.value));
    }
  };

  const filteredOptions = optionsData.filter((opt) =>
    opt.label.toLowerCase().includes(search.toLowerCase())
  );

  const handleClickOutside = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="multiselect-wrapper" ref={dropdownRef}>
      <div className="multiselect-header" onClick={() => setIsOpen(!isOpen)}>
        {selected.length === 0 ? 'Chọn vai trò...' : `${selected.length} đã chọn`}
        <span className="arrow">{isOpen ? '▲' : '▼'}</span>
      </div>

      {isOpen && (
        <div className="multiselect-dropdown">
          <input
            type="text"
            className="search-box"
            placeholder="Tìm kiếm..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <label className="checkbox">
            <input
              type="checkbox"
              checked={isAllSelected}
              onChange={toggleSelectAll}
            />
            Chọn tất cả
          </label>
          {filteredOptions.map((opt) => (
            <label key={opt.value} className="checkbox">
              <input
                type="checkbox"
                checked={selected.includes(opt.value)}
                onChange={() => toggleOption(opt.value)}
              />
              {opt.label}
            </label>
          ))}
        </div>
      )}
    </div>
  );
};

export default MultiSelectWithCheckbox;
