import { memo } from 'react';
import Select from 'react-select';

function Form( props ) {
    const { departments, onChange, handleSubmit, inputs, handleChangeSelect } = props;

    const options = departments?.map(item => ({
        value: item.Code,
        label: `${item.Code} - ${item.Name}`
    })) || [];
    return (
        <form className="row g-3" onSubmit = { handleSubmit }>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Từ ngày</label>
                <input type="date" style={styles.InputText} className="form-control" name="formDate" value={inputs.formDate || ""} onChange={(event)=>onChange(event)} />
            </div>
            <div className="col-md-6 col-sm-6">
                <label className="form-label" style={styles.InputText}>Chọn phòng ban</label>
                <Select
                    options={options}
                    onChange={handleChangeSelect}
                    placeholder="Chọn phòng ban"
                    styles={{ container: base => ({ ...base, ...styles.text }) }}/>
                {/* <select className="form-select" style={styles.text} onChange ={ handleChangeSelect }>
                    <option value="">Chọn phòng ban</option>
                    { departments && departments.length > 0  ? 
                      departments.map((item, index) => {
                      return ( <option key={index} value={item.Code}>{item.Code} - {item.Name}</option>) }) : ""  
                  }
                </select> */}
            </div>
            
            <div className="col-md-12 col-sm-12">
                <label className="form-label" style={styles.InputText}>Mục đích sử dụng</label>
                <textarea className="form-control" name="note" rows="5" cols="20" value={inputs.note || ""} onChange={(event)=>onChange(event)}/>
            </div>
            <br/>
            <div className="d-grid gap-3 col-md-6 col-sm-12 col-12">
                <button type="submit" style={styles.InputText} className="btn btn-outline-primary">Lọc</button>
            </div>
        </form>
    )
}

export default memo(Form);
const styles = {
 InputText:{
  fontFamily:"Times New Roman"
},
margin_top:{
    marginTop:"20px",
    fontFamily:"Times New Roman",
    marginBottom:"20px",
},
}
