function ModalItem( props ) {  
  const { orderHeader,handeOrderLine } = props
  return (
      orderHeader && orderHeader.length > 0 ?
      orderHeader.map((item,index) => {
      return (
        <tr key={index}>
           <td>{item.STT}</td>
           <td>{item.KhuVuc}</td>
           <td>{item.MaDH}</td>
           <td>{item.MaKH} <br></br>{item.TenKH}</td>
           <td>{ item.Status === "0" ? <button className="btn btn-outline-success" onClick={()=>handeOrderLine(item.MaDH)} data-bs-dismiss="modal">Chưa soạn</button>
               : item.Status === "1" ? <button className="btn btn-outline-danger" onClick={()=>handeOrderLine(item.MaDH)} data-bs-dismiss="modal">Đang soạn</button> 
               : <span style={{"color":"#800080"}} >Hoàn tất</span> 
               }</td>
        </tr>
      )
    })
    :
    <tr>
        <td colSpan={6} style={{'textAlign':'center',"color":"red"}}>Không có dữ liệu</td>
    </tr>
  );
}

export default ModalItem;
