import { getApi } from "../../api";
import * as Types from "../type/index"

/* ================== GET NAME USER ================== */
export const getUser = () => {
  return (dispatch) => {
    getApi("auth/profile").then((result) =>{
      //localStorage.setItem('profile', JSON.stringify(result.data));
      return dispatch({ type: Types.GETUSER, payload: result.data })
    })
  }
};
/* ================== GET NAME USER ================== */

/* ================== GET LIST STATUS ================== */
export const getStatus = () => {
  return (dispatch) => {
    getApi("status").then((result) =>{
      return dispatch({ type: Types.STATUS, payload: result.data })
    })
  }
};
/* ================== GET LIST STATUS ================== */

/* ================== GET ITEM ================== */
export const getItem = () => {
  return (dispatch) => {
    getApi("item").then((result) =>{
      return dispatch({ type: Types.GET_ITEM, payload: result.data })
    })
  }
};
/* ================== GET ITEM ================== */

/* ================== ORDER ================== */
export const addOrder = (products) => {
  return (dispatch) => {
    return dispatch({ type: Types.ADD_ORDER, payload: products })
  }
};

export const deleteOrder = (itemCode, variant) => {
  return (dispatch) => {
    return dispatch({ type: Types.DELETE_ORDER, payload: itemCode + variant })
  }
};

export const getCartByUser = () => {
  return (dispatch) => {
    getApi("cart/getbyuser").then((result) =>{
      return dispatch({ type: Types.GET_CART_BY_USER, payload: result.data })
    })
  }
};

export const addOrderPurchasing = () => {
  return (dispatch) => {
    getApi("cart/getbyuser").then((result) =>{
      return dispatch({ type: Types.GET_CART_BY_USER, payload: result.data })
    })
  }
};
/* ================== ORDER ================== */
