import { getApi } from "../../api";
import * as Types from "../type/index"

/* ============================================ LOCATION ============================================ */

/* get kho */
export const getLocation = () => {
  return (dispatch) => {
      getApi("getLocation").then((result) =>{
        return dispatch({ type: Types.GETLOCATION, payload: result.data })
      })
  }
};

/* ============================================ LOCATION ============================================ */

/* ============================================ ORDER ============================================ */

/* get Warehouse Shipment Order Detail */
export const getWSOrderDetail = () => {
    return (dispatch) => {
        getApi("getOrderDetail").then((result) =>{
          return dispatch({ type: Types.GETWSORDERDETAIL, payload: result })
