import { getApi, postApiv1 } from "../../api";
import * as Types from "../type/index"

/* ================== GET NAME USER ================== */
export const getUser = () => {
  return (dispatch) => {
    getApi("auth/profile").then((result) =>{
      return dispatch({ type: Types.GET_USER, payload: result.data })
    })
  }
};
/* ================== GET NAME USER ================== */

/* ================== GET ITEM ================== */
export const getItem = () => {
  return (dispatch) => {
    getApi("item").then((result) =>{
      return dispatch({ type: Types.GET_ITEM, payload: result.data })
    })
  }
};
/* ================== GET ITEM ================== */

/* ================== ORDER ================== */
export const addOrder = (products) => {
  return (dispatch) => {
    return dispatch({ type: Types.ADD_ORDER, payload: products })
  }
};

export const deleteOrder = (itemCode, variant) => {
  return (dispatch) => {
    return dispatch({ type: Types.DELETE_ORDER, payload: itemCode + variant })
  }
};

export const getCartByUser = () => {
  return (dispatch) => {
    getApi("cart/getbyuser").then((result) =>{
      return dispatch({ type: Types.GET_CART_BY_USER, payload: result.data })
    })
  }
};

export const addOrderPurchasing = () => {
  return (dispatch) => {
    getApi("cart/getbyuser").then((result) =>{
      return dispatch({ type: Types.GET_CART_BY_USER, payload: result.data })
    })
  }
};

export const approval = (formData) => {
  return async (dispatch) => {
    await postApiv1("orderpurchasing/approval", formData).then((result) =>{
      return dispatch({ type: Types.GET_ORDER_APPROVAL, payload: result.data })
    })
  }
};

export const updateStatus = (document) => {
  return (dispatch) => {
    return dispatch({ type: Types.UPDATE_ORDER_APPROVAL, payload: document })
  }
};

export const updateStatusCancel = (document) => {
  return (dispatch) => {
    return dispatch({ type: Types.CANCEL_ORDER_APPROVAL, payload: document })
  }
};

/* ================== ORDER ================== */
