import { getApi, postApi, postApiv1 } from "../../api";
import * as Types from "../type/index"

/* ================== thuế ================== */

/* lấy danh sách thuế */
export const getTax = () => {
  return (dispatch) => {
      getApi("tax").then((result) =>{
        return dispatch({ type: Types.GETTAX, payload: result.data })
      })
  }
};

/* ================== thuế ================== */

/* ================== user ================== */

/* get name user */
