import * as types from '../type/index';
import { findIndex } from '../../hooks';

const initialState =[]

const reducerBill = (state = initialState, action) => {
  
  const { type, payload } = action;  
  var temp = -1
  
  switch (type) {
    case types.GETBILLL:  
      payload ? state = payload :  state = []
      return [...state] 
    case types.UPDATE_STATUS_BILL: 
      temp = findIndex(state, payload)
      if(temp !== -1){
        state[temp].Status = 1
      }
      return [...state]
    default:
      return state
      
  }
}

export default reducerBill;
/**
 * reverse() được dùng để đảo ngược thứ tự của các phần tử trong mảng.
