import { getApi } from "../../api";
import * as Types from "../type/index"


/* lấy danh sách thuế */
export const getTax = () => {
  return (dispatch) => {
      getApi("tax").then((result) =>{
        return dispatch({ type: Types.GETTAX, payload: result.data })
      })
  }
};

/* lấy danh hóa đơn */
export const getBill = () => {
  return (dispatch) => {
      getApi("/getBill").then((result) =>{
        return dispatch({ type: Types.GETBILLL, payload: result.data })
      })
  }
};


/* ============================================ LOCATION ============================================ */

/* get kho */
export const getLocation = () => {
  return (dispatch) => {
      getApi("getLocation").then((result) =>{
        return dispatch({ type: Types.GETLOCATION, payload: result.data })
      })
  }
};

/* ============================================ LOCATION ============================================ */

/* ============================================ ORDER ============================================ */

/* get đơn hàng Header */
export const getOrderHeader = (locationCode,formDate,toDate) => {
    return (dispatch) => {
        getApi(`getOrderHeader/${locationCode}/${formDate}/${toDate}`).then((result) =>{
          return dispatch({ type: Types.GETORDERHEADER, payload: result.data })
        })
    }
};

/* get đơn hàng Line */
export const getOrderLine = (code) => {
  return (dispatch) => {
      getApi(`getOrderLine/${code}`).then((result) =>{
        return dispatch({ type: Types.GETORDERLINE, payload: result.data })
      })
  }
};

/* update trạng thái đơn hàng Line */
export const setStatusOrderLine = (codeOrder,codeItem,codeLot) => {
  return (dispatch) => {
      getApi(`updateStatusOrderLine/${codeOrder}/${codeItem}/${codeLot}`).then((result) =>{
        return dispatch({ type: Types.SETSTATUSORDERLINE, payload: result.data })
      })
  }
};

/* update trạng thái đơn hàng Header */
export const setStatusOrderHeader = (codeOrder) => {
  return (dispatch) => {
      getApi(`updateStatusOrderHeader/${codeOrder}`).then((result) =>{
        return dispatch({ type: Types.SETSTATUSORDERHEADER })
      })
  }
};

/* filter trạng thái đơn hàng Header */
export const filerStatusOrderHeader = (status) => {
  return (dispatch) => {
    return dispatch({ type: Types.FILERSTATUSORDERHEADER, payload: status })
  }
};

/* filter search đơn hàng Header */
export const filerSearchOrderHeader = (query) => {
  return (dispatch) => {
    return dispatch({ type: Types.FILERSEARCHORDERHEADER, payload: query })
  }
};

/* ============================================ ORDER ============================================ */


/*
 * Các Action: Khi ta định nghĩa các action, ta khai báo các tên của hành động trong ứng dụng. 
 * dispatch la 1 ham
