import { useState, useEffect } from 'react';
import ModalItem from "./ModalItem";
import { getApi } from '../../api';
function Modal( props ) {

    const [orderHeader, setOrderHeader] = useState();
    
    useEffect(() => {
        getApi('getOrderHeader/NK01,NK05').then((res)=>{
            setOrderHeader(res.data);
        })

      }, [props.location])

    return (
      <>
      {/* Button trigger modal */}
       <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
          Chon don hang soan hang
        </button>
        {/* Modal */}
        <div className="modal" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog modal-xl">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="staticBackdropLabel">Modal</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
              <table className="table table-striped table-hover">
                <thead>
                    <tr>
                        <th scope="col">STT</th>
                        <th scope="col">MaKho</th>
                        <th scope="col">MaDH</th>
                        <th scope="col">MaKH</th>
                        <th scope="col">TenKH</th>
                        <th scope="col">TrangThai</th>
                    </tr>
                </thead>
                <tbody>
                    <ModalItem orderHeader = { orderHeader ? orderHeader : null }/>
                </tbody>
              </table>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" className="btn btn-primary">SAVE</button>
              </div>
            </div>
          </div>
        </div>
        {/* Modal */}
      </>
    )
}

export default Modal;
