import { useEffect, useState } from 'react'
//import { useSelector, useDispatch } from 'react-redux'
//import * as ACTIONS from '../../store/actions/index'
import Table from './table';
import { getApi } from '../../api';

function Home() {
    
    // const order= useSelector((state) => state.order);
    // const dispatch = useDispatch()

    // useEffect(() => {
    //     dispatch(ACTIONS.getWSOrderDetail())
    //  },[dispatch])

    const [order, setOrder] = useState()
    const [dateHienTai, setDateHienTai] = useState()
    let date = new Date(), y = date.getFullYear(), m = date.getMonth() + 1;
    var firstDay = new Date(y, m, 1).toString("dd/MM/yyyy");
    setDateHienTai(date)
    console.log(firstDay)
    useEffect(() => {
        getApi("getOrderDetail").then((res)=>{
            setOrder(res.data);
        })
      }, [])
    
    return ( 
    <div className="row">
        <div class="cards ">
            <div class="card">
                <div className="input-groups">
                    <label className="col-form-label">Từ ngày</label>
                    <input type="date" value={dateHienTai} className="form-control"/>
                </div>
            </div>
            <div class="card">
                <div className="input-groups">
                    <label className="col-form-label">Từ ngày</label>
                    <input type="date" value={date} className="form-control"/>
                </div>
            </div>
        </div>
        
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">STT</th>
                    <th scope="col">Kho</th>
                    <th scope="col">Lot</th>
                    <th scope="col">Description</th>
                    <th scope="col">Variant Code</th>
                    <th scope="col">DVT</th>
                    <th scope="col">SL</th>
                </tr>
            </thead>
            <tbody>
                <Table order = { order ? order : null }/>
            </tbody>
        </table>
    </div>
    );
}

export default Home;
