import { useState, useEffect, useRef, memo } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Panel } from 'primereact/panel';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';
import { ScrollTop } from 'primereact/scrolltop';
import * as ACTIONS from '../../../store/actions/index';
import { getApi, postApiv1 } from '../../../api';
import Form from './form';
import Modal from './modal';

function Home() { 
  const order = useSelector((state) => state.order);
  const user = useSelector((state) => state.user); 
  const dispatch = useDispatch();
  
  const [item, setItem] = useState([]);
  const [inputs, setInputs] = useState({});
  const [number, setNumber] = useState();
  const [selectedOptionItem, setOptionItem] = useState(null);
  const toast = useRef(null);
  
  useEffect(() => {
    const getTodayAsYYYYMMDD = () => {
      const date = new Date();
      const y = date.getFullYear();
      const m = String(date.getMonth() + 1).padStart(2, '0');
      const d = String(date.getDate()).padStart(2, '0');
      return `${y}-${m}-${d}`;
    };

    setInputs({ formDate: getTodayAsYYYYMMDD() });
    
    const fetchItems = async () => {
      try {
        const result = await getApi("item");
        setItem(result.data);
      } catch (error) {
        console.error("Lỗi khi gọi API Item:", error);
      }
    };
    
    fetchItems();
  }, [])
  
  /* Lấy dữ liệu từ api */
  useEffect(() => {
    dispatch(ACTIONS.getCartByUser())
  }, [dispatch])
  
  /* Lấy dữ liệu từ thẻ input */
  const onChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }
  
  /* Lấy dữ liệu số lượng trong input */
  const handleChangeInputNumber = (event) =>{
    setNumber(event.value)
  }

  /* lấy dữ liệu sản phẩm */
  function handleChangeItem(selectedOption) {
    setOptionItem(selectedOption?.value);
  }

  /* xóa sản phẩm ra giỏ hàng */
  const handleChangeDeleteCart = async (itemCode, variant) => {
    try {
      const formData = new FormData();
      formData.append('itemcode', itemCode);
      formData.append('variant', variant);

      await postApiv1("cart/delete", formData);
      dispatch(ACTIONS.deleteOrder(itemCode, variant));
      toast.current.show({ severity: 'success', summary: 'Thông báo', detail: `Sản phẩm ${itemCode} - ${variant} đã được xóa.`, life: 3000 });
    } catch (error) {
      toast.current.show({ severity: 'error', summary: 'Lỗi', detail: `Không thể xóa sản phẩm ${itemCode}`, life: 3000 });
    }
  };

  const activityBodyTemplate = (rowData) => {
    return <Button type="button" severity="danger"
      onClick={() => handleChangeDeleteCart(rowData.itemCode, rowData.variant)}
      icon="pi pi-trash" rounded/>
  };
  
  /* thêm sản phẩm vào giỏ hàng */
  const handeModalAddCart = () => {
    if(!selectedOptionItem){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn sản phẩm", life: 3000});
    }
    if(!number){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền số lượng", life: 3000});
    }
    
    const parts = selectedOptionItem.split(' - ');
    if (parts.length !== 3) {
      console.log("Invalid format. Expected format: ItemCode - Variant - Unit"); 
    }
    const productName = item.find(p => p.Code === parts[0]);
    const products = [{ 
      STT: order.length + 1,
      itemCode: parts[0], 
      variant: parts[1], 
      itemName: productName.Name, 
      unit: parts[2], 
      quantity: number
    }];

    const isDuplicate = order.some(item => 
      item.itemCode === parts[0] && item.variant === parts[1]
    );
    
    if (isDuplicate) {
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Mã hàng này có rồi. Vui lòng chọn lại", life: 3000});
    }
    
    let formData = new FormData(); 
    formData.append('itemcode', parts[0]);
    formData.append('variant', parts[1]);
    formData.append('unit', parts[2]);   
    formData.append('quantity', number);    

    postApiv1("cart/add", formData).then(() =>{ })
    dispatch(ACTIONS.addOrder(products));
  }

  const AddOrderPurchasing = async () => {
    if(!inputs.formDate){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn ngày", life: 3000});
    }
    if(!inputs.supplier){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền nhà cung cấp", life: 3000});
    }
    if(!inputs.purpose){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền mục đích sử dụng", life: 3000});
    }
    if(!order || order.length === 0){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn sản phẩm để đặt hàng", life: 3000});
    }
    
    let document = '';
    try {
      const result = await getApi("orderpurchasing/maxdocument");
      document = result[0].DocumentNo;
    } catch (error) {
      return toast.current.show({severity: "error", summary: "Error", detail: "Không lấy được mã đơn hàng", life: 3000});
    }

    for (const key in order) {
      if (order.hasOwnProperty(key)) {
        const formData = new FormData();

        formData.append('document', document);
        formData.append('postingDate', inputs.formDate);
        formData.append('supplier', inputs.supplier);
        formData.append('intendedUse', inputs.purpose);
        formData.append('note', inputs.note ? inputs.note : '.');
        formData.append('itemcode', order[key].itemCode);
        formData.append('variant', order[key].variant);
        formData.append('unit', order[key].unit);
        formData.append('quantity', order[key].quantity);
        
        try {
          const res = await postApiv1("orderpurchasing/add", formData);
          dispatch(ACTIONS.deleteOrder(order[key].itemCode, order[key].variant));
          console.log(res);
        } catch (error) {
          return toast.current.show({severity: "error", summary: "Error", detail: "Đặt hàng thất bại", life: 3000});
        }
      }
    }
    setTimeout(alertFunc, 1000);
    function alertFunc() { 
      return toast.current.show({severity:"success", summary: "Success", detail:"Đặt hàng thành công.", life: 3000}); 
    }
  }
  
  return ( 
    <div className="row">
      <Panel style={styles.header} header="Yêu Cầu Hàng Hóa">
        <Form order = {order} user = {user} onChange = {onChange} inputs = {inputs} activityBodyTemplate = {activityBodyTemplate} 
              AddOrderPurchasing = {AddOrderPurchasing}/>
      </Panel>
      <Modal item = {item} selectedOptionItem = {selectedOptionItem} number = {number}
             handleChangeInputNumber = {handleChangeInputNumber}
             handeModalAddCart = {handeModalAddCart} 
             handleChangeItem = {handleChangeItem}/>
      <Toast ref = {toast}/>
      <ScrollTop/> 
    </div>
  );
}

export default memo(Home);
const styles = {
   header:{  fontFamily:"Times New Roman" }
}
