import { useEffect, useState, memo, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
import Modal from './modal';
import ShowDialog from './dialog';
import Form from './form';
import { postApiv1 } from '../../api';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Panel } from 'primereact/panel';
import { SelectButton } from 'primereact/selectbutton';
import { ScrollTop } from 'primereact/scrolltop';

function Bill() {
    const bill = useSelector((state) => state.bill); 
    const staff = useSelector((state) => state.staff); 
    const dispatch = useDispatch();

    const [inputs, setInputs] = useState({});
    const [dataEIDocumentNo, setdataEIDocumentNo] = useState();
    const [message, setMessage] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [loadingData, setLoadingData] = useState();
    const [loading, setLoading] = useState(false);
    const [visible, setVisible] = useState(false);
    const [value, setValue] = useState(null);
    const [selectStaff, setSelectStaff] = useState(null);
    const [number, setNumber] = useState();
    const [ingredient, setIngredient] = useState('');
    const [description, setDescription ] = useState('');
    const [selectedProducts, setSelectedProducts] = useState(null);
    const [dialogVisible, setDialogVisible] = useState(false);
    const [valueEI, setValueEI] = useState();
    const toast = useRef(null);

    const justifyOptions = [
        {icon: 'pi pi-lock-open', value: 0},
        {icon: 'pi pi-lock', value: 1},
        {icon: 'pi pi-server', value: 2}
    ];

    const justifyTemplate = (option) => {
        return <i className={option.icon}></i>;
    }

    /* get data staff */
    useEffect(() => {
        dispatch(ACTIONS.getStaffByUser()) 
    }, [dispatch])

    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    useEffect(() => {
        let date = new Date()
        var y = date.getFullYear()
        var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
        var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
        setInputs({'formDate':y + "-" + m + "-01", 'toDate':y + "-" + m + "-" + d });
    }, [])
    
    /* get data */
    const handleSubmit = (event) => {
        event.preventDefault();
        
        if(valueEI){
            if(valueEI === 'PSI'){
                setLoadingData(true);

                let formData = new FormData(); 
                formData.append('action', "BillPSI");
                formData.append('fromDate', inputs.formDate);
                formData.append('toDate', inputs.toDate);
             
                dispatch(ACTIONS.getBill(formData));
                setTimeout(alertFunc, 15000);
                function alertFunc() { setLoadingData(false) }
            }
            if(valueEI === 'PTS'){
                setLoadingData(true);

                let formData = new FormData(); 
                formData.append('action', "BillPTS");
                formData.append('fromDate', inputs.formDate);
                formData.append('toDate', inputs.toDate);

                dispatch(ACTIONS.getBill(formData));
                setTimeout(alertFunc, 2000);
                function alertFunc() { setLoadingData(false) }
            }
        }else{
            return toast.current.show({severity:'warn', summary: 'Warning', detail:'Vui lòng chọn chứng từ', life: 3000});
        }
    }
    
    /* get detail 1 item */
    const handeChangeStatusBillLine = (Code, DocumentNo, Quantity) => {
        let formData = new FormData()
        formData.append('code', Code);
        formData.append('documentNo', DocumentNo);
        setdataEIDocumentNo({Code, DocumentNo});
        setNumber(Quantity);

        postApiv1("getDetailBill", formData).then((result) =>{
            setMessage(result.data[0]);
        })
    }
    
    /* insert 1 row in [WS Out Of The Warehouse] */
    const handeInsertListOrderDelivered = () => {
        let formData = new FormData(); 
        formData.append('code', dataEIDocumentNo.Code);
        formData.append('documentNo', dataEIDocumentNo.DocumentNo);
        formData.append('staffCode', selectStaff);
        formData.append('quantity', number);
        formData.append('vehicle', ingredient);
        formData.append('note', description);
        
        postApiv1("insert", formData).then((result) =>{
            let message = result.data[0].Mes;
            switch (message) {
                case '0':
                    return toast.current.show({severity:'warn', summary: 'Warning', detail:`Hóa đơn ${dataEIDocumentNo.Code} đã giao rồi`, life: 3000});
                case '1':
                    return showSuccess(dataEIDocumentNo.Code, selectStaff, number);
                case '2':
                    return toast.current.show({severity:'warn', summary: 'Warning', detail:'User này không được phân quyền cho công việc này', life: 3000});
                default:
                    return "";
            }
        })

        setVisible(false);
        setDescription('');
        setNumber('');
        setIngredient('');
    }
    
    const showSuccess = (Code, selectStaff, number) => {
        toast.current.show({severity:'success', summary: 'Success', detail:`Hóa đơn ${Code} giao thành công`, life: 3000});
        dispatch(ACTIONS.updateStatusBill(Code, selectStaff, number));
    }
    
    const renderHeader = () => {
        return (
        <div className="flex justify-content-between">
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
            <div className="flex justify-content-center" style={styles.margin_top}>
                <SelectButton value={value} onChange={(e) => handeFilterStatus(e.value) } itemTemplate={justifyTemplate} optionLabel="value" options={justifyOptions} />           
            </div>
        </div>
        );
    };
    
    const renderLoading = () => {
        return loadingData === true ? (
            <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        )
        : "Không có dữ liệu"
    }

    const header = renderHeader();
    
    const getSeverity = (status, Code, DocumentNo, Quantity) => {
        switch (status) {
            case '0':
                return <Button type="button" data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeChangeStatusBillLine(Code, DocumentNo, Quantity)} icon="pi pi-cog" rounded></Button>;
            case '1':
                return <i className="pi pi-check" style={{ color: 'slateblue' }}></i>; 
            default:
                return "";
        }
    };
    
    /* get 1 item in table */
    const activityBodyTemplate = (rowData) => {
        return getSeverity(rowData.Status, rowData.Code, rowData.DocumentNo, rowData.Quantity)
    };

    
    const handeFilterStatus = (value) =>{
        setValue(value);
        switch (value) {
            case 0:
                return toast.current.show({ severity: 'info', summary: 'Trạng thái đơn hàng', detail: "Đơn hàng chưa giao"});
            case 1:
                return toast.current.show({ severity: 'info', summary: 'Trạng thái đơn hàng', detail: "Đơn hàng đã giao"});
            case 2:
                return toast.current.show({ severity: 'info', summary: 'Trạng thái đơn hàng', detail: "Tất cả"});
            default:
                return "";
        }
    }
   
    const handeCloseModal = () => {
        setVisible(false);
    }

    const handeOpenModal = () => {
        if(selectStaff){
            if(number){
                if(ingredient){
                    setVisible(true);
                }else{
                    toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn phương tiện", life: 3000});
                }
            }else{
                toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền số kiện", life: 3000});
            }
        }else{
            toast.current.show({severity:'warn', summary: 'Warning', detail:`Vui lòng chọn nhân viên giao hàng`, life: 3000});
        }
    }
   
    const handleChangeSelect = (event)  => {
        setSelectStaff(event.target.value)
    }
      
    const handleChangeRadio = (event) => {
        setIngredient(event.value)
    }
   
    const handleChangeInputNumber = (event) =>{
        setNumber(event.value)
    }

    const handleChangeDescription = (event) =>{
        setDescription(event.target.value);
    }
    
    const dialogFooterTemplate = () => {
        return ( 
        <div>
            <Button style={styles.dataTable} label="Thoát" icon="pi pi-times" onClick={ ()=> handeCloseBillAll() } className="p-button-text" severity="secondary" rounded text raised/>
             <Button label="Giao đơn hàng" icon="pi pi-check" onClick={()=>handeChangeBillAll()} loading={loading} style={styles.dataTable} severity="info" rounded/>
        </div> 
        )
    }
    
    const handeOpenBillAll = () => {
       selectedProducts ? setDialogVisible(true) : toast.current.show({severity:'warn', summary: 'Warning', detail:`Vui lòng chọn hóa đơn`, life: 3000});
    }

    const handeChangeBillAll = () =>{
        if(selectStaff){
                if(ingredient){
                    setLoading(true);
                    selectedProducts.forEach((data, index) => {
                        let formData = new FormData(); 
                        formData.append('code', data.Code);
                        formData.append('documentNo', data.DocumentNo);
                        formData.append('quantity', data.Quantity);
                        formData.append('staffCode', selectStaff);
                        formData.append('vehicle', ingredient);
                        formData.append('note', description);

                        postApiv1("insert", formData).then((result) =>{
                            let message = result.data[0].Mes;
                            switch (message) {
                                case '0':
                                    return toast.current.show({severity:'warn', summary: 'Warning', detail:`Hóa đơn ${data.Code} đã giao rồi`, life: 3000});
                                case '1':
                                    return showSuccess(data.Code, selectStaff, data.Quantity);
                                case '2':
                                    return toast.current.show({severity:'warn', summary: 'Warning', detail:'User này không được phân quyền cho công việc này', life: 3000});
                                default:
                                    return "";
                            }
                        })
                    });
                   
                    setTimeout(() => {
                        setDescription('');
                        setNumber('');
                        setIngredient('');
                        setDialogVisible(false);
                        setSelectedProducts(null);
                        setLoading(false);
                        setSelectStaff(null);
                    }, 1000);
                }else{
                    toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng chọn phương tiện", life: 3000});
                }
        }else{
            toast.current.show({severity:'warn', summary: 'Warning', detail:`Vui lòng chọn nhân viên giao hàng`, life: 3000});
        }
    }
   
    const handeCloseBillAll = () =>{
        setDialogVisible(false)
        setSelectedProducts(null)
    }

    return ( 
    <div className="row">
        <Panel header="Danh sách hóa đơn">
            <Form handleSubmit = { handleSubmit } onChange = { onChange } inputs = { inputs } valueEI = { valueEI } setValueEI = { setValueEI } />
            <ShowDialog dialogVisible = { dialogVisible } 
                                   selectedProducts = { selectedProducts }
                                   staff = { staff }
                                   ingredient = { ingredient } 
                                   description = { description }
                                   setSelectedProducts = { setSelectedProducts }
                                   handleChangeSelect = { handleChangeSelect }
                                   handleChangeRadio = { handleChangeRadio }
                                   handleChangeDescription = { handleChangeDescription}
                                   dialogFooterTemplate = { dialogFooterTemplate }
                                   handeOpenBillAll = { handeOpenBillAll }
                                   handeCloseBillAll = { handeCloseBillAll }/>
        </Panel>
        <DataTable value={ value=== 0 ? bill.filter((item) => item.Status.toLowerCase().includes("0")) : 
                                         value=== 1 ? bill.filter((item) => item.Status.toLowerCase().includes("1")) :
                                         bill} paginator showGridlines rows={200} 
                            globalFilter={globalFilter} header={header}
                            emptyMessage={renderLoading()} style={styles.dataTable}
                            selection={selectedProducts} onSelectionChange={(e) => setSelectedProducts(e.value)}>
             <Column selectionMode="multiple" headerStyle={{ width: '3rem' }}></Column>
            <Column field="Number" header="Số hóa đơn" sortable style={{ width: '5%' }}></Column>
            <Column field="CustomerName" header="Tên khách hàng" sortable style={{ width: '65%' }}></Column>
            <Column field="Quantity" header="Số kiện" sortable style={{ width: '5%' }}></Column>
            <Column field="DocumentDate" header="Ngày HĐ" sortable style={{ width: '10%' }}></Column>
            <Column field="CreatedDate" header="Ngày" sortable style={{ width: '10%' }}></Column>
            <Column field="Status" header="Trạng thái" sortable body={activityBodyTemplate} style={{ width: '10%' }}></Column>
        </DataTable> 
        <Modal message = {message ? message : null }
                      visible = { visible }
                      staff = { staff ? staff : null }
                      ingredient = { ingredient }
                      number = { number }
                      description = { description }
                      handeInsertListOrderDelivered = { handeInsertListOrderDelivered } 
                      handeCloseModal = { handeCloseModal } 
                      handeOpenModal = { handeOpenModal }
                      handleChangeSelect = { handleChangeSelect }
                      handleChangeRadio = { handleChangeRadio }
                      handleChangeInputNumber = { handleChangeInputNumber }
                      handleChangeDescription = { handleChangeDescription} />
        <Toast ref={toast} />
        <ScrollTop />
    </div>
    );
}

export default memo(Bill);

const styles = {
    title:{
        background: "#062544"
    },
    margin_top:{
        marginTop:"10px",
        fontFamily:"Times New Roman"
    },
    dataTable:{
        fontFamily:"Times New Roman"
    }
}
