import { getApi } from "../../api";
import * as Types from "../type/index"

/* ================== GET NAME USER ================== */
export const getUser = () => {
  return (dispatch) => {
    getApi("auth/profile").then((result) =>{
      return dispatch({ type: Types.GETUSER, payload: result.name })
    })
  }
};
/* ================== GET NAME USER ================== */

/* ================== GET LIST DEPARTMENTS ================== */
export const getDepartments = () => {
  return (dispatch) => {
    getApi("departments").then((result) =>{
      return dispatch({ type: Types.DEPARTMENTS, payload: result.data })
    })
  }
};
/* ================== GET LIST DEPARTMENTS ================== */

/* ================== GET LIST STAFF ================== */
export const getStaff = () => {
  return (dispatch) => {
    getApi("staff").then((result) =>{
      return dispatch({ type: Types.STAFF, payload: result.data })
    })
  }
};
/* ================== GET LIST STAFF ================== */

/* ================== GET LIST STATUS ================== */
export const getStatus = () => {
  return (dispatch) => {
    getApi("status").then((result) =>{
      return dispatch({ type: Types.STATUS, payload: result.data })
    })
  }
};
/* ================== GET LIST STATUS ================== */

/* ================== GET ITEM ================== */
export const getItem = () => {
  return (dispatch) => {
    getApi("item").then((result) =>{
      return dispatch({ type: Types.GET_ITEM, payload: result.data })
    })
  }
};
/* ================== GET ITEM ================== */

/* ================== ORDER ================== */
export const addOrder = (optionItem, quantity) => {
  const parts = optionItem.split(' - ');
  if (parts.length !== 3) {
      console.log("Invalid format. Expected format: ItemCode - Variant - Unit"); 
  }
  const product = item.find(p => p.Code === parts[0]);
  const products = [
    { 
      itemCode: parts[0], 
      variant: parts[1], 
      itemName: product.Name, 
      unit: parts[2], quantity
    }];

  return (dispatch) => {
    return dispatch({ type: Types.ADD_ORDER, payload: products })
  }
};

export const deleteOrder = (itemCode) => {
  return (dispatch) => {
    return dispatch({ type: Types.DELETE_ORDER, payload: itemCode })
  }
};

/* ================== ORDER ================== */
