import * as types from '../type/index';
import { findIndex_v1 } from '../../hooks';

const initialState =[]
const reducerWarehouseShipment = (state = initialState, action) => {
  const { type, payload } = action;  
  var temp = -1
  switch (type) {
    case types.GET_WAREHOUSE_SHIPMENT:  
      payload ? state = payload :  state = []
      return [...state] 
    case types.UPDATE_IMG_WAREHOUSE_SHIPMENT: 
      
      temp = findIndex_v1(state, payload.Code)
      if(temp !== -1){
        state[temp].Status =  "1"
        state[temp].Image =  payload.Image
      } 
      return [...state]
    default:
      return state
  }
}

export default reducerWarehouseShipment;
