import Bill from '../pages/Bill';
import Login from '../pages/Login';
import Home from '../pages/Home';
import Tax from '../pages/Tax';
import Error404 from '../components/Error404';
import HeaderOnly from '../components/Layout/HeaderOnly';

var publicRoutes = []

if(JSON.parse(localStorage.getItem('access_token')) === null){
    publicRoutes =[
        { path: '/', component: Login, layout: null },
        { path: '/thue', component: Tax, layout: null },
        { path: '*', component: Error404, layout:  null}
    ]
}else{
    publicRoutes = [
        { path: '/', component: Login, layout: null },
        { path: '/home', component: Home, layout: HeaderOnly },
        { path: '/bill', component: Bill, layout:  HeaderOnly},
        { path: '*', component: Error404, layout:  null}
    ]
}

const privateRoutes =[]

export { publicRoutes, privateRoutes }
