import { useState, useEffect, memo } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Panel } from 'primereact/panel';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';
import * as ACTIONS from '../../store/actions/index';
import Form from './form';

function Home() {
  const item = useSelector((state) => state.item);
  const departments = useSelector((state) => state.departments); 
  const staff = useSelector((state) => state.staff); 
  const status = useSelector((state) => state.status); 
  const dispatch = useDispatch();

  const [inputs, setInputs] = useState({});
  const [selectstatus, setStatus] = useState(null);
  const [selectedValues, setSelectedValues] = useState({
    department: null,
    staff: null,
  });
  const toast = useRef(null);
  
  useEffect(() => {
    let date = new Date()
    var y = date.getFullYear()
    var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
    var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
    setInputs({ 'formDate':y + "-" + m + "-" + d });
  }, [])
  
  /* Lấy dữ liệu từ api */
  useEffect(() => {
    dispatch(ACTIONS.getDepartments()) 
    dispatch(ACTIONS.getStaff()) 
    dispatch(ACTIONS.getStatus()) 
    dispatch(ACTIONS.getItem()) 
  }, [dispatch])
  
  /* Lấy dữ liệu từ thẻ input */
  const onChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }
  
  /* Lấy dữ liệu từ thẻ select */
  const handleChangeSelect = (event) => {
    setStatus(event.target.value)
  }
  
  const handleChange = (key) => (selectedOption) => {
    setSelectedValues(prev => ({
      ...prev,
      [key]: selectedOption,
    }));
  };
  
  /* thao tác xóa */
  const handeChangeDelete = (itemcode) => {
    return toast.current.show({severity:'success', summary: 'Success', detail: `Hóa đơn ${itemcode} đã giao rồi`, life: 3000});
        //console.log(itemcode);
  }

  const getSeverity = (itemcode) => {
     return <Button type="button" onClick={()=>handeChangeDelete(itemcode)} icon="pi pi-trash" rounded></Button>;
        // switch (status) {
        //     case '0':
        //         return <Button type="button" data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeChangeStatusBillLine(Code, DocumentNo, Quantity)} icon="pi pi-cog" rounded></Button>;
        //     case '1':
        //         return <i className="pi pi-check" style={{ color: 'slateblue' }}></i>; 
        //     default:
        //         return "";
        // }
  };

  const activityBodyTemplate = (rowData) => {
        return getSeverity(rowData.ItemCode, rowData.Unit, rowData.Quantity)
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    let formData = new FormData(); 
    formData.append('fromDate', inputs.formDate);
    formData.append('note', inputs.note);
    console.log("fromDate: " + inputs.formDate + " - NCC:" + inputs.supplier + " - note: " + inputs.note + " - selectstatus: " + selectstatus);
    console.log("purpose: " + inputs.purpose);
    
    console.log("department: " + selectedValues.department.value + " - staff: " + selectedValues.staff.value);
                
    // dispatch(ACTIONS.getBill(formData));
    // setTimeout(alertFunc, 15000);
    // function alertFunc() { setLoadingData(false) }
  }

  return ( 
    <div className="row">
      <Panel style={styles.header} header="ORDERD PURCHASING">
        <Form departments = { departments } staff = { staff } status = { status }
              item = { item }
              handleSubmit = { handleSubmit } onChange = { onChange } inputs = { inputs }
              handleChange = { handleChange }
              handleChangeSelect = { handleChangeSelect }
              activityBodyTemplate = { activityBodyTemplate }  />
      </Panel>
    </div>
  );
}

export default memo(Home);
const styles = {
   header:{  fontFamily:"Times New Roman" }
}
