import * as types from '../type/index';

const initialState =[]
const reducerItem = (state = initialState, action) => {
  const { type, payload } = action; 
  switch (type) {
    case types.GET_ITEM:
      console.log(payload);
      
      payload ? state = payload :  state = []
      return [...state] 
    default:
      return state
  }
}

export default reducerItem;
