import * as types from '../type/index';
import { findIndex_v1, search } from '../../hooks';

const initialState =[]

const reducerBill = (state = initialState, action) => {
  const { type, payload } = action;  
  var temp = -1
  switch (type) {
    case types.GETBILLL:  
      payload ? state = payload :  state = []
      return [...state] 
    case types.GET_WAREHOUSE_SHIPMENT:  
      payload ? state = payload :  state = []
      return [...state] 
    case types.UPDATE_STATUS_BILL: 
      let date = new Date()
      var y = date.getFullYear()
      var m = (date.getMonth() + 1) > 9 ? (date.getMonth() + 1) : "0" + (date.getMonth() + 1)
      var d = date.getDate() > 9 ? date.getDate() : "0"+date.getDate()
      
      temp = findIndex_v1(state, payload.Code)
      if(temp !== -1){
        state[temp].Status = "1"
        state[temp].CreatedDate = d + "-" + m + "-" + y
        state[temp].StaffCode = payload.staff
        state[temp].Quantity = payload.number
      } 
      return [...state]
    case types.FITER_STATUS:
      state = search(state, payload)
      return [...state]
    // case types.RESET_BILL:
    //   state = []
    //   return [...state]
    // case types.GET_BILL_BY_STAFF:
    //   payload ? state = payload :  state = []
    //   return [...state]
    default:
      return state
  }
}

export default reducerBill;
