import * as types from '../type/index';

const initialState =[];

const reducerUser = (state = initialState, action) => {
  
  const { type, payload } = action;  
  
  switch (type) {
    case types.GETUSER: 
      state = [];
      state.push(payload);
      return [...state]
    default:
      return state
      
  }
}

export default reducerUser;

/**
 * reverse() được dùng để đảo ngược thứ tự của các phần tử trong mảng.
