import * as types from '../type/index';

const initialState =[];

const reducerLocation = (state = initialState, action) => {
  
  const { type, payload } = action;  
  
  switch (type) {
    case types.GETLOCATIONBYUSER: 
      payload ? state = payload :  state = []
      return [...state] 
    default:
      return state
      
  }
}

export default reducerLocation;
