import axios from 'axios';
const API_URL='http://171.244.205.210:8500/bitex/api/';
//const API_URL='http://localhost:8500/bitex/api/';

const ourRequest = axios.CancelToken.source();

export const getApi = async (endpoint) => {
  try {
    let res = await axios.get(`${API_URL}${endpoint}`,{
      headers: { Accept: 'text/html, application/json, text/plain, */*' }
    },{CancelToken:ourRequest.token})  
    return res.data
  } catch (error){
    console.log("error API", error)
  }
};

export const postApi = async (endpoint,data) => {
  try {
    let res = await axios({ method: 'POST',url:`${API_URL}${endpoint}`,data: data, headers: { Accept: 'text/html, application/json, text/plain, */*' }}) 
    return res.data
  } catch (error) {
    console.log("error API", error);
  }
};

export const putApi = async (endpoint,data) => {
  try {
    let res = await axios({ method: 'PUT',url:`${API_URL}${endpoint}`,data:data},{CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
    console.log("error API");
  }
};

export const deleteApi = async (endpoint) => {
  try {
    let res = await axios({ method: 'DELETE',url:`${API_URL}${endpoint}`,data:null},{CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
    console.log("error API");
  }
};

