  try {
    let res = await axios({ method: 'POST',
                          url:`${API_URL}${endpoint}`,
                          headers :{'Content-Type': 'multipart/form-data'},
                          data:data},
                          {CancelToken:ourRequest.token})   
    return res.data
  } catch (error) {
