import * as types from '../type/index';

const initialState =[];

const reducerLogin = (state = initialState, action) => {
  
  const { type, payload } = action;  
  
  switch (type) {
    case types.LOGIN: 
      localStorage.setItem('user', JSON.stringify(payload));
      return payload
    default:
      return state
      
  }
}

export default reducerLogin;

