import { combineReducers } from 'redux'
import reducerTax from './tax';
import reducerBill from './bill';
import reducerLogin from './login';
import reducerLocation from './location';
import reducerStaff from './staff';
import reducerMenu from './menu';

const rootReducer = combineReducers({
  tax: reducerTax,
  bill: reducerBill,
  login: reducerLogin,
  location: reducerLocation,
  staff: reducerStaff,
  menu: reducerMenu,
})

export default rootReducer;

/*
Thông thường, ứng dụng sẽ có nhiều reducer nên bạn phải gộp tất cả reducer lại để bỏ vào trong store. 
sử dụng hàm combineReducer của redux để hợp nhất tất cả reducer thành 1 reducer là rootReducer
*/
