//import { Link } from "react-router-dom"
//import { useDispatch } from 'react-redux'
//import {url} from '../../Api/api'
//import * as ACTIONS from '../../store/actions/index'
//import {formatPrice} from "../../hooks"
function Table( props ) {

    // const dispatch = useDispatch()
    // const onAddToCart = (product) => {
    //   dispatch(ACTIONS.addCart(product,1,35))
    // }

    return (
      props.order &&
      props.order.map((item,index) => {
      return (
        <tr key={index}>
           <td>{item.MaSP}</td>
           <td>{item.TenSP}</td>
           <td>{item.Variant}</td>
           <td>{item.MaLo}</td>
           <td>{item.SL}</td>
           <td>{item.DVT}</td>
           <td hidden>{item.MaDH}</td>
           <td>{ item.Status === 1 ? <button type="button" className="btn btn-outline-success">Đã Lấy</button> 
                                   : <button type="button" className="btn btn-outline-info" >Chưa Lấy</button>
            }</td>
        </tr>
      )
    })
   
  );
}

export default Table;

