import {  memo, useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
import { postApi } from '../../api';
import { Panel } from 'primereact/panel';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { ScrollTop } from 'primereact/scrolltop';
import Modal from './modal';
import ShowDialog from './dialog';

function Staff() {
  const bill = useSelector((state) => state.bill); 
  const dispatch = useDispatch();

  const [image, setImage] = useState();
  const [showImage, setShowImage] = useState();
  const [globalFilter, setGlobalFilter] = useState(null);
  const [modalEI, setmodalEI] = useState(null);
  const [selectedBill, setSelectedBill] = useState(null);
  const [dialogVisible, setDialogVisible] = useState(false);
  const toast = useRef(null);

  useEffect(() => {
   let formData = new FormData(); 
   formData.append('user',  JSON.parse(localStorage.getItem('user')));
   dispatch(ACTIONS.getBillByStaff(formData)) 
  }, [dispatch])

  const imageBodyTemplate = (bill) => {
    return <img src={ bill.Image ? `http://171.244.205.210:8500/bitex/storage/app/public/images/${bill.Image}` : "" } alt={bill.Image} width={75} height={75} style={styles.image}  />;
  };

  const renderHeader = () => {
   return (
   <div className="flex justify-content-between">
       <span className="p-input-icon-left">
           <i className="pi pi-search" />
           <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
       </span>
   </div>
   );
};

const header = renderHeader();

const activityBodyTemplate = (rowData) => {
 return getSeverity(rowData.EI, rowData.Number, rowData.Document, rowData.SO, rowData.Name, rowData.Status)
};

const getSeverity = (EI, Number,  Document, Name, Status, SO) => {
 switch (Status) {
     case '0':
         return <Button type="button" data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeShowModal(EI, Number, Name, Document, SO)} icon="pi pi-truck" rounded></Button>; 
     case '1':
        return <Button type="button" data-bs-toggle="modal" data-bs-target="#ThongBao" onClick={()=>handeShowModal(EI, Number, Name, Document, SO )} icon="pi pi-ellipsis-v"></Button>;  
     default:
         return "";
 }
};
 const handeShowModal = (EI, Number, Name, Document, SO) =>{
  setmodalEI({EI, Number, Name, Document, SO});
 }

  const handeExit = () => {
    setImage('')
    setShowImage('')
  }
  const handeSaveImage = () => {
    if(modalEI){
      if(image){
         let formData = new FormData();
         formData.append('image', image);
         formData.append('EI', modalEI.EI);
         formData.append('Document', modalEI.Document);
         formData.append('user', JSON.parse(localStorage.getItem('user')));
         
         postApi("insertBillByStaff", formData).then((result) =>{
          var message = result.data[0].Mess;
          var img = result.data[0].Image;
          switch (message) {
              case '0':
                  return updateWarning(modalEI.EI, img);
              case '1':
                   return showSuccess(modalEI.EI, img);
              case '2':
                  return toast.current.show({severity:'warn', summary: 'Warning', detail:'User này không được phân quyền cho công việc này', life: 3000});
              default:
                  return "";
          }
      })
         setImage('')
         setShowImage('')
         
       }else{  return toast.current.show({severity:'warn', summary: 'Warning', detail:'Vui lòng chọn hình ảnh', life: 3000}); }
     }else{  return toast.current.show({severity:'error', summary: 'Error', detail:'Error', life: 3000}); }
   }

   const showSuccess = (EI, img) => {
    toast.current.show({severity:'success', summary: 'Success', detail:`Hóa đơn ${EI} giao thành công`, life: 3000});
    dispatch(ACTIONS.updateStatusBillByStaff(EI, img));
   }
 
   const updateWarning = (EI, img) => {
    toast.current.show({severity:'warn', summary: 'Warning', detail:`Hóa đơn ${EI} cập nhập thành công`, life: 3000})
    dispatch(ACTIONS.updateStatusBillByStaff(EI, img));
   }

   const handeOpenBillAll = () => {
    selectedBill ? setDialogVisible(true) : toast.current.show({severity:'warn', summary: 'Warning', detail:`Vui lòng chọn hóa đơn`, life: 3000});
   }

   const handeCloseBillAll = () =>{
    setDialogVisible(false)
    setSelectedBill(null)
    setImage('')
    setShowImage('')
  }

  const dialogFooterTemplate = () => {
    return ( 
    <div>
        <Button style={styles.dataTable} label="Thoát" icon="pi pi-times" onClick={ ()=> handeCloseBillAll() } className="p-button-text" severity="secondary" rounded text raised/>
         <Button label="Lưu" icon="pi pi-check" onClick={()=>handeChangeBillAll()} style={styles.dataTable} severity="info" rounded/>
    </div> 
    )
   }

   const handeChangeBillAll = () => {
    if(image){
        selectedBill.forEach((data) => {
           let formData = new FormData();
           formData.append('image', image);
           formData.append('EI', data.EI);
           formData.append('Document', data.Document);
           formData.append('user', JSON.parse(localStorage.getItem('user')));
           postApi("insertBillByStaff", formData).then((result) =>{
            var message1 = result.data[0].Mess;
            var img1 = result.data[0].Image;
            switch (message1) {
                case '0':
                    return updateWarning(data.EI, img1);
                case '1':
                     return showSuccess(data.EI, img1);
                case '2':
                    return toast.current.show({severity:'warn', summary: 'Warning', detail:'User này không được phân quyền cho công việc này', life: 3000});
                default:
                    return "";
            }
        })
        });
        setImage('')
        setShowImage('')
        setSelectedBill(null)
        setDialogVisible(false)
         
    }else{  return toast.current.show({severity:'warn', summary: 'Warning', detail:'Vui lòng chọn hình ảnh', life: 3000}); }
   }
 
    return ( 
    <div className="row">
        <Panel style={styles.dataTable} header="Danh sách hóa đơn giao hàng">
        <ShowDialog dialogVisible = { dialogVisible } 
                               selectedBill = { selectedBill }
                               setSelectedBill = { setSelectedBill }
                               bill = { bill }
                               handeOpenBillAll = { handeOpenBillAll }
                               handeCloseBillAll = { handeCloseBillAll }
                               dialogFooterTemplate = { dialogFooterTemplate}
                               showImage = { showImage }
                               setShowImage = { setShowImage }
                               setImage = { setImage }
                             />
        </Panel>
        <DataTable value={bill} paginator showGridlines rows={200} 
                           globalFilter={globalFilter} header={header}
                           emptyMessage="Không có dữ liệu" style={styles.dataTable}
                           selection={selectedBill} onSelectionChange={(e) => setSelectedBill(e.value)} >
            <Column selectionMode="multiple" headerStyle={{ width: '3rem' }}></Column>
            <Column field="Number" header="Số hóa đơn" sortable style={{ width: '10%' }}></Column>
            <Column field="SO" header="Số SO" sortable style={{ width: '10%' }}></Column>
            <Column field="Name" header="Tên khách hàng" sortable style={{ width: '60%' }}></Column>
            <Column header="Hình" body={imageBodyTemplate} style={{ width: '10%' }}></Column>
            <Column field="Status" header="Trạng thái" sortable body={activityBodyTemplate} style={{ width: '10%' }}></Column>
        </DataTable>
        <Modal handeSaveImage = { handeSaveImage } handeExit = { handeExit }
                      image = { image } setImage = { setImage }
                      showImage = { showImage } setShowImage = { setShowImage } modalEI = { modalEI }/>
        <Toast ref={toast} />
        <ScrollTop />
    </div>
    );
}

export default memo(Staff);

const styles = {
    title:{ background: "#062544" },
    margin_top:{ marginTop:"10px", fontFamily:"Times New Roman" },
    dataTable:{  fontFamily:"Times New Roman" },
    image: {borderRadius: "50px"}
}
