import { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import * as ACTIONS from '../../store/actions/index'
import Table from './table';

function Home() {
    var date = new Date(), y = date.getFullYear(), m = date.getMonth();
    var firstDay = new Date(y, m, 1).toString("dd/MM/yyyy");
    var lastDay = new Date(y, m + 1, 0).toString("dd/MM/yyyy");
    // console.log(firstDay);
    // console.log(lastDay);
    const order= useSelector((state) => state.order);
    const dispatch = useDispatch()
   
    useEffect(() => {
        dispatch(ACTIONS.getWSOrderDetail())
     },[dispatch])

    return ( 
    <div className="row">
        <div className="col">
            <div className="input-groups col-2">
                <label className="col-form-label">Từ ngày</label>
                <input type="date" className="form-control"/>
            </div>
            <div className="input-groups col-2">
                <label className="col-form-label">Đến ngày</label>
                <input type="date" className="form-control"/>
            </div>
        </div>
        
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">STT</th>
                    <th scope="col">Kho</th>
                    <th scope="col">Lot</th>
                    <th scope="col">Description</th>
                    <th scope="col">Variant Code</th>
                    <th scope="col">DVT</th>
                    <th scope="col">SL</th>
                </tr>
            </thead>
            <tbody>
                <Table order = { order ? order : null }/>
            </tbody>
        </table>
    </div>
    );
}

export default Home;
