import { combineReducers } from 'redux'
import reducerUser from './user';
import reducerLogin from './login';
import reducerDepartments from './departments';
import reducerStaff from './staff';

const rootReducer = combineReducers({
  user: reducerUser,
  login: reducerLogin,
  departments: reducerDepartments,
  staff: reducerStaff
})

export default rootReducer;
