import { memo } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';

function Modal(props) {
     const { detailOrder } = props;
 
    return (
    <div className="modal fade" id="detail" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={styles.text} id="staticBackdropLabel">Chi tiết đặt hàng</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" />
              </div>
              <div className="modal-body">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                  <DataTable value={detailOrder} emptyMessage="Không có dữ liệu" style={styles.dataTable}>
                            <Column field="DocumentNo" header="Mã" sortable style={{ width: '10%' }}></Column>
                            <Column field="Department" header="Phòng ban" sortable style={{ width: '30%' }}></Column>
                            <Column field="PostingDate" header="Ngày cần hàng" sortable style={{ width: '15%' }}></Column>
                            <Column field="CreatedDate" header="Ngày tạo" sortable style={{ width: '15%' }}></Column>
                            <Column field="StatusName" header="Trạng thái" sortable style={{ width: '15%' }}></Column>
                            <Column field="Status" header="Thao tác" body={activityBodyTemplate} style={{ width: '15%' }}></Column>
                          </DataTable>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" style={styles.text}>Thoát</button>
                {/* <button type="button" className="btn btn-outline-success"  data-bs-dismiss={ selectedOptionItem && number ? "modal" : "" }  onClick = {handeModalAddCart} style = {styles.text}>Đặt hàng</button> */}
              </div>
            </div>
          </div>
    </div>
    )
}

export default memo(Modal);

const styles = {
  bodyModal: {
    borderRadius: "10px",
    boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
    padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  },
  dataTable: {fontFamily:"Times New Roman"},
}
