import { memo, useState, useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Panel } from 'primereact/panel';
import { Toast } from 'primereact/toast';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import * as ACTIONS from '../../../store/actions/index';
import { postApiv1 } from '../../../api';
import Form from './form';
import Modal from './modal';

function Item() {
  const item = useSelector((state) => state.item);
  const dispatch = useDispatch();
  
  const [inputs, setInputs] = useState({});
  const [checked, setChecked] = useState(false);
  const [globalFilter, setGlobalFilter] = useState(null);
  const toast = useRef(null);

  useEffect(() => {
    dispatch(ACTIONS.getItemAll());
  }, [dispatch]);
  
  const onChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }
  
  const handleSubmit = (event) => {
    event.preventDefault();

    if(!inputs.name){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền tên sản phẩm", life: 3000});
    }
    if(!inputs.unit){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền đơn vị tính", life: 3000});
    }

    try {
      let formData = new FormData(); 
      formData.append('name', inputs.name);
      formData.append('unit', inputs.unit);
      dispatch(ACTIONS.insertItem(formData));
      setInputs({})
    } catch (error) {
      return toast.current.show({severity:"error", summary: "Error", detail:"Lỗi khi thêm dữ liệu", life: 3000});
    }
  }

  const handeModalUpdateItem = async() => {
    if(!inputs.name){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền tên sản phẩm", life: 3000});
    }
    if(!inputs.unit){
      return toast.current.show({severity:"warn", summary: "Warning", detail:"Vui lòng điền đơn vị tính", life: 3000});
    }
    try {
      let formData = new FormData(); 
      formData.append('name', inputs.name);
      formData.append('unit', inputs.unit);
      formData.append('status', checked ? 1 : 0);
      formData.append('id', inputs.id);
      await postApiv1('item/update', formData);
      dispatch(ACTIONS.getUpdateItem(inputs.id, inputs.name, inputs.unit, checked ? 1 : 0));
      setInputs({})
    } catch (error) {
      return toast.current.show({severity:"error", summary: "Error", detail:"Lỗi khi cập nhập dữ liệu", life: 3000});
    }
  }
  
  const handeChangeEit = (data) => {
    setInputs(values => ({...values, name: data.ItemName, unit: data.Unit, id: data.ID}));
    setChecked(data.Status)
  }

  const activityBodyTemplate = (rowData) => {
      return (<button className="btn btn-outline-info" style={styles.InputText} data-bs-toggle="modal" data-bs-target="#editItem" onClick={()=>handeChangeEit(rowData)}><i className="pi pi-file-edit"></i></button>)  
  };

  const renderHeader = () => {
    return (<div className="flex justify-content-between">
              <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText style={styles.InputText} type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
              </span>
            </div>);
  };
  return ( 
    <div className="row">
      <Panel style={styles.header} header="Tạo sản phẩm">
        <Form inputs = {inputs} onChange = {onChange}
              handleSubmit = {handleSubmit}/>
        <br/>
        <DataTable value={item} paginator showGridlines rows={25} 
                   globalFilter={globalFilter} header={renderHeader()}
                   emptyMessage="Không có dữ liệu" style={styles.dataTable}>
          <Column field="ID" header="ID" hidden></Column>
          <Column field="ItemCode" header="Mã sản phẩm" sortable style={{ width: '15%' }}></Column>
          <Column field="Variant" header="Mã màu" sortable style={{ width: '15%' }}></Column>
          <Column field="ItemName" header="Tên sản phẩm" sortable style={{ width: '50%' }}></Column>
          <Column field="Unit" header="ĐVT" sortable style={{ width: '10%' }}></Column>
          <Column field="Status" header="Trạng thái" sortable style={{ width: '10%' }}></Column>
          <Column field="Action" header="Thao tác" body={activityBodyTemplate} style={{ width: '15%' }}></Column>
        </DataTable>
        <Modal inputs = {inputs} checked = {checked} setChecked = {setChecked}
               onChange = {onChange} handeModalUpdateItem = {handeModalUpdateItem}/>
      </Panel>
      <Toast ref = {toast}/>
    </div>
  );
}

export default memo(Item);
const styles = {
  header: {fontFamily:"Times New Roman"},
  InputText: {fontFamily:"Times New Roman"},
  dataTable: {fontFamily:"Times New Roman"},
  title:{
    textAlign:"center",
    color:"red",
    padding:"10px",
    fontFamily:"Times New Roman"
  },
  margin_top:{
    marginTop:"20px",
    fontFamily:"Times New Roman",
    marginBottom:"20px",
  },
}
