import { useEffect, useState } from 'react'
//import { useSelector, useDispatch } from 'react-redux'
//import * as ACTIONS from '../../store/actions/index'
import Table from './table';
import Modal from './modal';
import { getApi } from '../../api';
//import { MultiSelect } from 'primereact/multiselect';
function Home() {
    
    // const order= useSelector((state) => state.order);
    // const dispatch = useDispatch()

    // useEffect(() => {
    //     dispatch(ACTIONS.getWSOrderDetail())
    //  },[dispatch])

    const [order, setOrder] = useState();
    const [location, setLocation] = useState();
    const [inputs, setInputs] = useState({});
    const [message, setMessage] = useState();
    const [orderCode, setorderCode] = useState();

     /* get input đưa vào setInputs  */ 
    const onChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}));
    }

    //const [dateHienTai, setDateHienTai] = useState()
    // let date = new Date(), y = date.getFullYear(), m = date.getMonth() + 1;
    // var firstDay = new Date(y, m, 1).toString("dd/MM/yyyy");
    // //setDateHienTai(date)
    // console.log(firstDay)
    const handeChange = (orderNo) => {
        getApi("getOrderDetail/" + orderNo).then((res)=>{
            setOrder(res.data);
        })
    }

    useEffect(() => {
        getApi("getLocation").then((res)=>{
            setLocation(res.data);
        })
      }, [])

      let formData = new FormData(); 
      formData.append('location', inputs.location);
      
    const handeChangeStatus = (MaDH,MaSP,MaLo) => {
        if(MaDH && MaSP && MaLo){
            getApi("updateStatus/"+ MaDH + "/" + MaSP + "/" + MaLo).then((res)=>{
                res.data ? setMessage(res.data.Message) : setMessage("Error")
            })
        }else{
            setMessage("thieu thong tin")
        }
        setorderCode(MaDH)
    }
    const handeChangeFinish = (e) => {
        console.log(orderCode);
    }
    console.log(message);
    return ( 
    <div className="row">
        <div className="cards ">
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Từ ngày</label>
                    <input type="date" className="form-control" name="formDate" onChange={(event)=>onChange(event)} />
                </div>
            </div>
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Đến ngày</label>
                    <input type="date" className="form-control" name="toDate" onChange={(event)=>onChange(event)}/>
                </div>
            </div>
            <div className="card">
                <div className="input-groups">
                    <label className="col-form-label">Kho</label>
                    {/* <MultiSelect optionLabel="name" optionValue="NO" value={location} options={location} onChange={(event) => onChange(event)} /> */}
                    <select className="form-select" id="location" name="location" onChange={(event)=>onChange(event)}>
                        { location && 
                          location.map((item,index) => {
                            return (<option value={item.NO} key={index}>{item.Name}</option>)
                          })
                        }
                    </select>
                </div>
                { /*<button type="button" className="btn btn-outline-primary" onClick={(event)=>onChangeSubmit(event)}>Submit</button> */}
            </div>
        </div>
        <div className="col-12">
        <Modal location = { inputs.location ? inputs.location : null } handeChange = { handeChange }/>
        </div>
        <table className="table table-striped table-hover">
            <thead>
                <tr>
                    <th scope="col">Mã sản phẩm</th>
                    <th scope="col">Tên sản phẩm</th>
                    <th scope="col">Màu sắc</th>
                    <th scope="col">Lô</th>
                    <th scope="col">Số Lượng</th>
                    <th scope="col">Đơn Vị Tính</th>
                    <th scope="col">Trạng Thái</th>
                </tr>
            </thead>
            <tbody>
                <Table order = { order ? order : null } handeChangeStatus = { handeChangeStatus }/>
            </tbody>
        </table>
        <button className="btn btn-outline-dark" onClick={(e) => handeChangeFinish(e)}>Hoàn tất đơn soạn</button>
        
        <div className="position-fixed bottom-0 end-0 p-3" style={{zIndex: 11}}>
          <div id="liveToast" className="toast hide" role="alert" aria-live="assertive" aria-atomic="true">
            <div className="toast-header">
              {/* {<img src="..." className="rounded me-2" alt="..." />} */}
              <strong className="me-auto">Thông báo</strong>
              {/* <small>ago</small> */}
              <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close" />
            </div>
            <div className="toast-body">
              {message}
            </div>
          </div>
        </div>
    </div>
    );
}

export default Home;
