import { memo } from 'react';

function Modal( props ) {
 const { handeSaveImage, handeExit , setImage, setShowImage, image, showImage, modalEI } = props;

 const uploadImage = (event) => {
   if(event.target.files && event.target.files.length > 0){
      setImage(event.target.files[0])
      setShowImage(URL.createObjectURL(event.target.files[0]))
    }
 }

    return (
    <div className="modal fade" id="ThongBao" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex={-1} aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" style={styles.text} id="staticBackdropLabel">Chi tiết hóa đơn</h5>
                <button type="button" className="btn-close" data-bs-dismiss="modal" onClick={ handeExit } aria-label="Close" />
              </div>
              <div className="modal-body">
              <form className="row g-3">
                <div className="card flex justify-content-center" style={styles.bodyModal}>
                   <div className="col-md-12">
                      <span style={styles.text}> Hóa đơn: { modalEI ? modalEI.EI : ""}</span><br/>
                      <span style={styles.text}> Số hóa đơn: { modalEI ? modalEI.Number : ""}</span><br/>
                      <span style={styles.text}>  Chứng từ: { modalEI ? modalEI.Document : ""}</span><br/>
                      <span style={styles.text}>  Số SO: { modalEI ? modalEI.SO : ""}</span><br/>
                      <span style={styles.text}> Tên khách hàng: { modalEI ? modalEI.Name : ""}</span>
                   </div>
                   <div className="col-md-12">
                        <label className="form-label" style={styles.text}>Image</label>
                        <input type="file" className="form-control" style={styles.input} size = {[400, 200]} placeholder='Upload an image' onChange={(event)=>uploadImage(event)}/>
                   </div>
                   <div className="col-md-12">
                       {showImage &&  
                        <img src={showImage} style={styles.image} height="180px" width="200px" alt="img"/>
                      }
                  </div>
                </div>
                </form>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-outline-secondary" data-bs-dismiss="modal" style={styles.text} onClick={ handeExit }>Thoát</button>
                <button type="button" className="btn btn-outline-primary"  data-bs-dismiss= { image ? "modal" : ""} style={styles.text} onClick={ handeSaveImage }>Lưu</button>
              </div>
            </div>
          </div>
    </div>
    )
}
//pi-image pi-images pi-times-circle
export default memo(Modal);

const styles = {
  bodyModal: {
  borderRadius: "10px",
  boxShadow: "5px 5px 15px rgba(0, 0, 0, 0.5)",
  padding: "20px"
  },
  text : {
    fontFamily:"Times New Roman",
    fontWeight: 500
  },
  image: { borderRadius:"10px", marginTop: "10px"},
  input: { fontFamily:"Times New Roman"}
}
