import { useState } from "react";
function Table( props ) {
  const { orderLine } = props
  const [thongbao, setThongbao] = useState()
  /* thay doi trang thai chi tiet don hang */
  const handeChangeStatusOrderLine = (codeOrder,codeItem,codeLot) => {
    codeOrder && codeItem && codeLot ?
    dispatch(ACTIONS.setStatusOrderLine(codeOrder,codeItem,codeLot)) :
    setThongbao("Lưu thành công")
  }
  console.log(thongbao);
    return (
      orderLine && orderLine.length > 0 ?
      orderLine.map((item,index) => {
      return (
        <tr key={index}>
           <td>{ item.IdxByNo === "1" ? item.MaSP : "" }</td>
           <td>{item.TenSP}</td>
           <td>{item.Variant}</td>
           <td>{item.MaLo}</td>
           <td>{item.SL}</td>
           <td>{item.DVT}</td>
           <td>{ item.Status==="0" ? <button className='btn btn-outline-info' onClick={()=>handeChangeStatusOrderLine(item.MaDH,item.MaSP,item.MaLo)} data-bs-toggle="modal" data-bs-target="#ThongBao">Chưa Lấy</button> 
               : item.Status==="1" ? <p className="text-primary">Đã Lấy</p> : ""
            }</td>
        </tr>
      )
    })
    :
    <tr>
        <td colSpan={7} style={{'textAlign':'center',"color":"red"}}>Không có dữ liệu</td>
    </tr>
  );
}

export default Table;
