import { useState, memo, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import * as ACTIONS from '../../store/actions/index';
//import { postApi } from '../../api';
import { Toast } from 'primereact/toast';

function Login() {
 // const user = useSelector((state) => state.login); 
  const dispatch = useDispatch();
  const [inputs, setInputs] = useState({});
  const navigate = useNavigate();
  const toast = useRef(null);

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}));
  }

  const showError = () => {
    toast.current.show({severity:'error', summary: 'Error', detail:'Đăng nhập thất bại!', life: 3000});
  }
  
  const handleSubmit = (event) => {
    event.preventDefault();
    let formData = new FormData(); 
    formData.append('userName', inputs.username);
    formData.append('Password', inputs.password);
    
    dispatch(ACTIONS.getLogin(formData));
    localStorage.setItem('user', JSON.stringify(inputs.username));

    if(JSON.parse(localStorage.getItem('user')) === "0" || JSON.parse(localStorage.getItem('user')) === null){
      showError();
    }else{
      navigate("/bill", {replace: true});
    }
    // else{
    //   navigate("/bill", {replace: true});
    //   navigate("/bill", {replace: true});
    // }
    //postApi("login", formData).then((result) =>{
    //localStorage.setItem('user', JSON.stringify(result.data[0].Code)) 
  }


 return ( 
  <div className="login">
     <div className="login-box">
          <div className="logo">
             <img src="img/logoBitex.png" alt="Logo" />
          </div>
          <form onSubmit={handleSubmit}>
          <div className="form-group input-container">
               <input type="text" className="input-field" onChange={handleChange} name="username" value={inputs.username || ""}  required/>
               <label className="input-label">Tên đăng nhập</label>
           </div>
           <div className="form-group input-container">
                <input type="password" className="input-field" onChange={handleChange} name="password" value={inputs.password || ""} required/>
                <label className="input-label">Mật khẩu</label>
            </div>
            <input type="submit" value="Đăng nhập"  />
      </form>
      <Toast ref={toast} />
     </div>
   </div>
   
 );
}

export default memo(Login);
