import Tax from '../pages/Tax';
import Bill from '../pages/Bill';
import Staff from '../pages/Staff';
import Login from '../pages/Login';
import Error404 from '../components/Error404';
import HeaderOnly from '../components/Layout/HeaderOnly';

var publicRoutes = []

if(JSON.parse(localStorage.getItem('user')) === "0" || JSON.parse(localStorage.getItem('user')) === null){
    publicRoutes =[
        { path: '/', component: Tax, layout:  null},
        { path: '/login', component: Login, layout:  null},
        { path: '/bill/staff', component: Staff, layout:  null},
        { path: '*', component: Error404, layout:  null}
    ]
}else{
    publicRoutes =[
        { path: '/', component: Tax, layout:  null},
        { path: '/bill', component: Bill, layout:  HeaderOnly},
        { path: '/bill/staff', component: Staff, layout:  null},
        { path: '/login', component: Login, layout:  null},
        { path: '*', component: Error404, layout:  null}
    ]
}

const privateRoutes =[]

export { publicRoutes, privateRoutes }
