import * as types from '../type/index';

const initialState =[]
const reducerPurchasingOrder = (state = initialState, action) => {
  const { type, payload } = action;  
  switch (type) {
    case types.CHANGE_PURCHASING_ORDER:
      payload ? state = payload :  state = []
      return [...state]
    case types.UPDATE_STATUS_PURCHASING_ORDER:
      return state.map(order => order.document === payload ? { ...order, status: 'Chờ duyệt' } : order);
    case types.CANCEL_STATUS_PH:
      return state.map(order => order.document === payload ? { ...order, status: 'Hủy' } : order); 
    default:
      return state
  }
}

export default reducerPurchasingOrder;
