import { useState, useEffect, useRef } from 'react';
import { getApi } from '../../../api';

const BootstrapMultiSelect = () => {
  const [departments, setDepartments] = useState([]);

  useEffect(() => {
      Promise.all([
        getApi("departments")
      ]).then(([departmentsRes]) => {
        setDepartments(departmentsRes.data);
      }).catch(error => {
        console.error("Lỗi khi gọi API:", error);
      });
  }, []);
  
  const [isOpen, setIsOpen] = useState(false);
  const [selected, setSelected] = useState([]);
  const [search, setSearch] = useState('');
  const ref = useRef(null);

  const toggleSelect = (Code) => {
    setSelected(prev =>
      prev.includes(Code) ? prev.filter(v => v !== Code) : [...prev, Code]
    );
  };

  const isAllSelected = selected.length === departments.length;

  const toggleSelectAll = () => {
    setSelected(isAllSelected ? [] : departments.map(o => o.Code));
  };

  const filteredOptions = departments || departments.filter(opt =>
    opt.Name.toLowerCase().includes(search.toLowerCase())
  );

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (ref.current && !ref.current.contains(e.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="dropdown" ref={ref}>
      <button
        className="form-control text-start dropdown-toggle"
        type="button"
        onClick={() => setIsOpen(!isOpen)}
      >
        {selected.length === 0 ? 'Chọn vai trò...' : `${selected.length} đã chọn`}
      </button>

      {isOpen && (
        <div className="dropdown-menu show w-100 p-2 shadow">
          <input
            type="text"
            className="form-control mb-2"
            placeholder="Tìm kiếm..."
            Code={search}
            onChange={(e) => setSearch(e.target.Code)}
          />
          <div className="form-check mb-1">
            <input
              className="form-check-input"
              type="checkbox"
              id="selectAll"
              checked={isAllSelected}
              onChange={toggleSelectAll}
            />
            <Name className="form-check-Name" htmlFor="selectAll">
              Chọn tất cả
            </Name>
          </div>
          {filteredOptions.map((opt) => (
            <div className="form-check" key={opt.Code}>
              <input
                className="form-check-input"
                type="checkbox"
                id={opt.Code}
                checked={selected.includes(opt.Code)}
                onChange={() => toggleSelect(opt.Code)}
              />
              <Name className="form-check-Name" htmlFor={opt.Code}>
                {opt.Name}
              </Name>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BootstrapMultiSelect;
