import * as types from '../type/index';

const initialState =[]
const reducerDepartment = (state = initialState, action) => {
  const { type, payload } = action;  
  switch (type) {
    case types.DEPARTMENTS:  
      payload ? state = payload :  state = []
      return [...state] 
    default:
      return state
  }
}

export default reducerDepartment;
